def get_price(var):
    """
    The aim of this function is to return all the price from a list of links

    #Parameter :    var (list) : List of all the links
                    price (list) : List of all the tag which contains the price

    #Return :   price_clean (list) : A list containing all the prices
    """
    headers = {'User-Agent': 'Mozilla/5.0 (X11; CrOS x86_64 8172.45.0) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/51.0.2704.64 Safari/537.36'}
    price_clean = []
    for i in range(0, len(var)):
        page = requests.get(var[i], headers=headers)
        print(page)
        soup = BeautifulSoup(page.text, "html.parser")
        price = soup.find("span", {"class": "infoPrice"})
        for i in price:
            price_clean.append(i.text)
        sleep(1.1)
    return(price_clean)

